definition.script:
pipeline {
    agent any
    tools {
        go 'Go 1.16'
    }
    options {
        timestamps()
    }
    stages{
        stage('Download Git repo') {
            steps {
                git 'https://github.com/wickett/word-cloud-generator.git'
            }
        }
        stage ('Test'){
            steps{
                sh '''sed -i "s/1.DEVELOPMENT/1.$BUILD_NUMBER/g" static/version
                      make lint
                      make test
                '''                
            }
        }
        stage ('Build code'){
            steps{
                sh '''
                      GOOS=linux GOARCH=amd64 go build -o ./artifacts/word-cloud-generator -v .
                      gzip -f artifacts/word-cloud-generator
                      ls -l artifacts/
                '''
            }
        }
        stage ('Nexus upload'){
            steps{
                nexusArtifactUploader (
                    nexusVersion: 'nexus3',
                    protocol: 'http',
                    nexusUrl: 'nexus:8081',
                    groupId: "jenkinsfile_build",
                    version: '1.$BUILD_NUMBER',
                    repository: 'word-cloud-build',
                    credentialsId: 'nexus-creds',
                    artifacts: [
                        [artifactId: 'word-cloud-generator',
                        type: 'gz',
                        classifier: '',
                        file: 'artifacts/word-cloud-generator.gz'
                        ]
                    ]
                )
            }
        }
	}
}
